/**
 * æ—¶é—´æ ¼å¼åŒ–
 * @param value
 * @param fmt
 * @returns {*}
 */
export function formatDate(value, fmt) {
    //å¦‚æœæ—¶é—´æ ¼å¼å«æœ‰Tï¼Œyyyy-MM-ddThh:mm:ss,yyyy-MM-ddThh:mm:ss.SSSZï¼Œè¿™æ ·åšå¯ä»¥è‡ªåŠ¨æŠŠ+0:00æ—¶åŒºçš„æ—¶é—´è½¬ä¸º+8:00çš„æ—¶é—´
    if (typeof value == 'string' && value.includes('T')) {
        value = new Date(value);
    }

    //æ­£åˆ™è¡¨è¾¾å¼
    var regPos = /^\d+(\.\d+)?$/;

    if (regPos.test(value) || value instanceof Date) {
        //å¦‚æœæ˜¯æ•°å­—
        let getDate = value instanceof Date ? value : new Date(value);
        let o = {
            'M+': getDate.getMonth() + 1,
            'd+': getDate.getDate(),
            'h+': getDate.getHours(),
            'm+': getDate.getMinutes(),
            's+': getDate.getSeconds(),
            'q+': Math.floor((getDate.getMonth() + 3) / 3),
            S: getDate.getMilliseconds(),
        };
        if (/(y+)/.test(fmt)) {
            fmt = fmt.replace(
                RegExp.$1,
                (getDate.getFullYear() + '').substr(4 - RegExp.$1.length)
            );
        }
        for (let k in o) {
            if (new RegExp('(' + k + ')').test(fmt)) {
                fmt = fmt.replace(
                    RegExp.$1,
                    RegExp.$1.length === 1 ?
                    o[k] :
                    ('00' + o[k]).substr(('' + o[k]).length)
                );
            }
        }

        try {
            fmt = fmt.replace('T', ' ');
        } catch (error) {
            console.log('formate date error : ' + error);
        }

        return fmt;
    } else {
        //TODO
        try {
            if (typeof value == 'undefined' || value == null) {
                value = '--';
            }
            value = value.trim();
            fmt = value.substr(0, fmt.length);
            fmt = fmt.replace('T', ' ');
        } catch (error) {
            console.log('formate date error : ' + error);
        }

        return fmt;
    }
}

